import React, { useState } from 'react';
import './Navbar.css';
import logo from './logo.png';

function Navbar({ setActiveCategory }) {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const handleMouseEnter = () => {
    setIsDropdownOpen(true);
  };

  const handleMouseLeave = () => {
    setIsDropdownOpen(false);
  };

  const handleCategorySelect = (category) => {
    setActiveCategory(category); // Update the active category to display the respective data
  };

  return (
    <div className="container">
      <header className="d-flex flex-wrap align-items-center justify-content-center justify-content-md-between py-3 mb-4 border-bottom">
        <div className="col-md-3 mb-2 mb-md-0">
          <a href="/" className="d-inline-flex link-body-emphasis text-decoration-none">
            <img src={logo} alt="Logo" height={100} width={100} />
          </a>
        </div>

        <ul className="nav col-12 col-md-auto mb-2 justify-content-center mb-md-0">
          <li><a href="#" onClick={() => handleCategorySelect("home")} className="nav-link px-2 link-dark">Home</a></li>
          
          {/* Dropdown for Shop */}
          <li
            className="nav-link px-2 link-dark position-relative"
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
          >
            Shop
            {isDropdownOpen && (
              <ul className="dropdown-menu show position-absolute">
                <li><a href="#" onClick={() => handleCategorySelect("mens")} className="dropdown-item">Mens</a></li>
                <li><a href="#" onClick={() => handleCategorySelect("womens")} className="dropdown-item">Womens</a></li>
                <li><a href="#" onClick={() => handleCategorySelect("newArrivals")} className="dropdown-item">New Arrivals</a></li>
              </ul>
            )}
          </li>

          <li><a href="#" onClick={() => handleCategorySelect("collections")} className="nav-link px-2 link-dark">Collections</a></li>
          <li><a href="#" onClick={() => handleCategorySelect("about")} className="nav-link px-2 link-dark">About</a></li>
          <li><a href="#" onClick={() => handleCategorySelect("cart")} className="nav-link px-2 link-dark">Cart</a></li>
          
          <li><a href="#" onClick={() => handleCategorySelect("contact")}className="nav-link px-2 link-dark">Contact Us</a></li>
        </ul>

        <div className="col-md-3 text-end">
          <button type="button" className="btn btn-outline-dark me-2">Login</button>
          <button type="button" className="btn btn-dark signup">Sign-up</button>
        </div>
      </header>
    </div>
  );
}

export default Navbar;
