import React, { useState, useEffect } from "react";
import mensWearData from './mens.json';
import womensWearData from './womens.json';
import newarrivals from './newarrivals.json';
import { useCart } from '../Cart/Context'; // Import the Cart Context
import './Mens.css';

const ProductCard = ({ product }) => {
  const handleAddToWishlist = () => {
    console.log(`${product.name} added to wishlist`);
  };

  const { addToCart } = useCart(); // Use the Cart Context

  return (
    <div className="card" style={{ width: "18rem", margin: "10px" }}>
      <img src={product.imageUrl} className="card-img-top" alt={product.name} height={300} width={300} />
      <div className="card-body">
        <h5 className="card-title">{product.name}</h5>
        <p className="card-text">Price: ₹{product.price}</p>
        <div className="card-actions">
          <button onClick={() => addToCart(product)} className="btn btn-dark">
            <i className="fas fa-shopping-cart"></i> Add to Cart
          </button>
          <button onClick={handleAddToWishlist} className="btn btn-light">
            <i className="fas fa-heart"></i> Add to Wishlist
          </button>
        </div>
      </div>
    </div>
  );
};

const CategoryButton = ({ category, isActive, onClick }) => (
  <button
    className={`category-btn ${isActive ? 'active' : ''}`}
    onClick={onClick}
  >
    {category}
  </button>
);

const MensWearPage = ({ category }) => {
  const [data, setData] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [sortOrder, setSortOrder] = useState(null);
  const { cart } = useCart(); // Access cart from context

  useEffect(() => {
    switch (category) {
      case "mens":
        setData(mensWearData.mensWear);
        break;
      case "womens":
        setData(womensWearData.womensWear);
        break;
      case "newarrivals":
        setData(newarrivals.newArrivals);
        break;
      default:
        setData([]);
    }
  }, [category]);

  const filteredItems = selectedCategory === 'All'
    ? data.flatMap(cat => cat.items)
    : data
        .filter(cat => cat.category === selectedCategory)
        .flatMap(cat => cat.items);

  const sortedItems = [...filteredItems].sort((a, b) => {
    if (sortOrder === 'asc') return a.price - b.price;
    if (sortOrder === 'desc') return b.price - a.price;
    return 0;
  });

  return (
    <div className="container">
      {/* Category Buttons */}
      <div className="category-buttons">
        <CategoryButton
          category="All"
          isActive={selectedCategory === 'All'}
          onClick={() => setSelectedCategory('All')}
        />
        {data.map(cat => (
          <CategoryButton
            key={cat.id}
            category={cat.category}
            isActive={selectedCategory === cat.category}
            onClick={() => setSelectedCategory(cat.category)}
          />
        ))}
      </div>

      {/* Sort Options */}
      <div className="sort-container">
        <button
          onClick={() => setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc')}
          className="sort-button"
        >
          Sort by Price ({sortOrder === 'asc' ? 'Lowest to Highest' : 'Highest to Lowest'})
        </button>
      </div>

      {/* Product Cards */}
      <div className="products">
        {sortedItems.map(product => (
          <ProductCard key={product.name} product={product} />
        ))}
      </div>

      {/* Cart Section */}
      <div className="cart-section">
        <h2>Cart</h2>
        {cart.length === 0 ? (
          <p>Your cart is empty.</p>
        ) : (
          <ul>
            {cart.map((item, index) => (
              <li key={index}>{item.name} - ₹{item.price}</li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default MensWearPage;
