import React, { useEffect, useState } from "react";
import axios from "axios";
import ''

function Account() {
  const [userDetails, setUserDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [formValues, setFormValues] = useState({
    name: "",
    mobile: "",
    profileImage: "",
  });

  useEffect(() => {
    // Get the logged-in user's emailOrMobile from local storage
    const user = JSON.parse(localStorage.getItem("user"));
    const emailOrMobile = user?.emailOrMobile;

    if (!emailOrMobile) {
      alert("User not logged in");
      return;
    }

    // Fetch user details from the server
    axios
      .get(`http://localhost:5000/api/user/${emailOrMobile}`)
      .then((response) => {
        setUserDetails(response.data);
        setFormValues({
          name: response.data.name || "",
          mobile: response.data.mobile || "",
          profileImage: response.data.profileImage || "",
        });
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching user details:", error);
        setLoading(false);
      });
  }, []);

  const nameFromEmail = userDetails?.emailOrMobile?.split('@')[0];

  if (loading) {
    return <p>Loading...</p>;
  }

  if (!userDetails) {
    return <p>No user details found.</p>;
  }

  return (
    <div>
      <h2>Account Details</h2>
      <div>
        <img
          src={userDetails.profileImage || "default-profile.png"}
          alt="Profile"
          style={{ width: "100px", borderRadius: "50%" }}
        />
        <p><strong>Name:</strong> {nameFromEmail}</p>
        <p><strong>Mobile:</strong> {userDetails.mobile}</p>
        <p><strong>Email:</strong> {userDetails.emailOrMobile}</p>
      </div>

      {/* Account actions card */}
      <div className="card mt-4">
        <div className="card-body">
          <h5 className="card-title">Account Actions</h5>
          <ul>
            <li>
              <a href="/cart">Your Cart</a>
            </li>
            <li>
              <a href="/orders">Your Orders</a>
            </li>
            <li>
              <a href="/update-profile">Update Profile</a>
            </li>
          </ul>
        </div>
      </div>
    </div>
  );
}

export default Account;
