import React, { useState, useEffect } from "react";
import mensWearData from './mens.json';
import womensWearData from './womens.json';
import './mens.css'

const ProductCard = ({ product }) => (
  <div className="card">
    <img src={product.imageUrl} alt={product.name} />
    <h5>{product.name}</h5>
    <p>Price: â‚¹{product.price}</p>
  </div>
);

const MensWomensWearPage = () => {
  const [selectedCategory, setSelectedCategory] = useState("All");
  const [activeGender, setActiveGender] = useState("mens");
  const [data, setData] = useState([]);

  useEffect(() => {
    setData(activeGender === "mens" ? mensWearData.mensWear : womensWearData.womensWear);
  }, [activeGender]);

  const filteredItems = selectedCategory === "All"
    ? data.flatMap((category) => category.items)
    : data.find((category) => category.category === selectedCategory)?.items || [];

  return (
    <div className="container">
      <div className="toggle-buttons">
        <button onClick={() => setActiveGender("mens")}>Men's Wear</button>
        <button onClick={() => setActiveGender("womens")}>Women's Wear</button>
      </div>
      <div className="category-buttons">
        <button onClick={() => setSelectedCategory("All")}>All</button>
        {data.map((category) => (
          <button key={category.id} onClick={() => setSelectedCategory(category.category)}>
            {category.category}
          </button>
        ))}
      </div>
      <div className="products">
        {filteredItems.map((item, index) => (
          <ProductCard key={index} product={item} />
        ))}
      </div>
    </div>
  );
};

export default MensWomensWearPage;
