import React, { useState, useEffect } from 'react';
import './Account.css';

const Account = () => {
  const [editMode, setEditMode] = useState(false);
  const [formValues, setFormValues] = useState({
    name: '',
    mobile: '',
    address: '',
    profileImage: '',
  });

  const [userDetails, setUserDetails] = useState({
    emailOrMobile: '',
    mobile: '',
    address: '',
  });

  const [nameFromEmail, setNameFromEmail] = useState('');

  useEffect(() => {
    // Fetch user details from the server
    const fetchUserDetails = async () => {
      try {
        const response = await fetch('/api/user-details');
        const data = await response.json();
        setUserDetails(data);
        setNameFromEmail(data.emailOrMobile.split('@')[0]);
      } catch (error) {
        console.error('Error fetching user details:', error);
      }
    };
    fetchUserDetails();
  }, []);

  const handleProfileChange = (e) => {
    setFormValues((prevValues) => ({
      ...prevValues,
      [e.target.name]: e.target.value,
    }));
  };

  const handleProfileUpdate = () => {
    // Update user profile on the server
    // ...
    setEditMode(false);
  };

  return (
    <div className="account-container">
      <div className="profile-card">
        <div className="profile-details">
          <img
            src={formValues.profileImage || 'https://via.placeholder.com/100'}
            alt="Profile"
          />
          <div className="personal-info">
            {editMode ? (
              <div>
                <input
                  type="text"
                  name="name"
                  value={formValues.name}
                  onChange={handleProfileChange}
                  placeholder="Enter your name"
                  className="form-control"
                />
                <input
                  type="text"
                  name="mobile"
                  value={formValues.mobile}
                  onChange={handleProfileChange}
                  placeholder="Enter your mobile"
                  className="form-control"
                />
                <input
                  type="text"
                  name="address"
                  value={formValues.address}
                  onChange={handleProfileChange}
                  placeholder="Enter your address"
                  className="form-control"
                />
                <input
                  type="text"
                  name="profileImage"
                  value={formValues.profileImage}
                  onChange={handleProfileChange}
                  placeholder="Enter profile image URL"
                  className="form-control"
                />
                <button className="btn btn-primary" onClick={handleProfileUpdate}>
                  Save Changes
                </button>
              </div>
            ) : (
              <div>
                <p><strong>Name:</strong> {nameFromEmail}</p>
                <p><strong>Mobile:</strong> {userDetails.mobile}</p>
                <p><strong>Address:</strong> {userDetails.address}</p>
                <p><strong>Email:</strong> {userDetails.emailOrMobile}</p>
                <button className="btn btn-secondary" onClick={() => setEditMode(true)}>
                  Edit Profile
                </button>
              </div>
            )}
          </div>
        </div>
      </div>

      <div className="actions-card">
        <h3 className="card-title">Account Actions</h3>
        <ul>
          <li>
            <button className="btn-link">Change Password</button>
          </li>
          <li>
            <button className="btn-link">Deactivate Account</button>
          </li>
          <li>
            <button className="btn-danger">Delete Account</button>
          </li>
        </ul>
      </div>
    </div>
  );
};

export default Account;
