import React, { useContext, useState, useEffect } from "react";
import { CartContext } from "../Cart/CartContext"; // Import CartContext
import kidsCollectionData from './kids.json';
import homeFurnishingsData from './homefurnishing.json';
import festiveWeddingData from './festive.json';
import officeUniformsData from './office.json';
import seasonalCollectionData from './seasonal.json';
import './collections.css';

const ProductCard = ({ product }) => {
  const { addToCart } = useContext(CartContext); // Use CartContext

  const handleAddToCart = () => {
    addToCart(product); // Add product to cart
  };

  const handleAddToWishlist = () => {
    console.log(`${product.name} added to wishlist`);
  };

  return (
    <div className="card" style={{ width: "18rem", margin: "10px" }}>
      <img src={product.imageUrl} className="card-img-top" alt={product.name} height={300} width={300} />
      <div className="card-body">
        <h5 className="card-title">{product.name}</h5>
        <p className="card-text">Price: â‚¹{product.price}</p>
        <div className="card-actions">
          <button onClick={handleAddToCart} className="btn btn-dark">
            <i className="fas fa-shopping-cart"></i> Add to Cart
          </button>
          <button onClick={handleAddToWishlist} className="btn btn-light">
            <i className="fas fa-heart"></i> Add to Wishlist
          </button>
        </div>
      </div>
    </div>
  );
};

const CollectionsPage = () => {
  const [data, setData] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('All');

  useEffect(() => {
    const categories = [
      kidsCollectionData.kidsCollection,
      homeFurnishingsData.homeFurnishings,
      festiveWeddingData.festiveWedding,
      officeUniformsData.officeUniforms,
      seasonalCollectionData.seasonalCollection
    ];
    setData([].concat(...categories)); // Combine all collections
  }, []);

  return (
    <div className="container">
      <div className="products">
        {data.map((item) => (
          <ProductCard key={item.name} product={item} />
        ))}
      </div>
    </div>
  );
};

export default CollectionsPage;
