import mensWearData from '../Shop/mens.json';
import womensWearData from '../Shop/womens.json';
import newArrivalsData from '../Shop/newarrivals.json';
const Cart = ({ cart = [] }) => {
  const allProducts = [...mensWearData, ...womensWearData];

  // Fetch product details from JSON
  const cartItems = cart.map((cartItem) => {
    return allProducts.find((product) => product.id === cartItem.id);
  });

  console.log("Cart Items:", cartItems); // Debugging log

  return (
    <div className="cart-section">
      <h2>Cart</h2>
      {cartItems.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <ul>
          {cartItems.map((item, index) => (
            <li key={index}>
              {item.name} - â‚¹{item.price}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Cart;
