import React, { useContext, useState } from "react";
import { CartContext } from "./CartContext.jsx";
import { useOrder } from "./OrderContext.jsx";

const Cart = () => {
  const { cart, updateQuantity, clearCart } = useContext(CartContext);
  const { addOrder } = useOrder();

  // State to handle the address
  const [address, setAddress] = useState({
    village: "",
    streetNo: "",
    city: "",
    state: "",
    pincode: "",
  });

  // Handle input change for address
  const handleChange = (e) => {
    const { name, value } = e.target;
    setAddress(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  // Handle form submission to place the order
  const handleSubmit = () => {
    if (!address.village || !address.city || !address.state || !address.pincode) {
      alert("Please fill in all address details.");
      return;
    }

    const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0) + 50;

    const order = {
      items: cart,
      total,
      address,
      orderDate: new Date().toLocaleString(),
    };

    addOrder(order);
    alert("Order placed successfully!");
    clearCart();
    setAddress({ village: "", streetNo: "", city: "", state: "", pincode: "" });
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">Your Cart</h2>
      {cart.length === 0 ? (
        <div className="d-flex flex-column align-items-center justify-content-center text-center py-5 border rounded">
          <img
            src="https://th.bing.com/th/id/OIP.GTpAJzc2mjH1AwOQgrda3AHaHa?w=200&h=201&c=7&r=0&o=5&pid=1.7"
            alt="Empty Cart"
            className="mb-3"
          />
          <h5 className="mb-3">Uh-Oh! Your cart appears to be empty!</h5>
          <a href="/shop" className="btn btn-dark">
            Shop Now
          </a>
        </div>
      ) : (
        <div className="row">
          <div className="col-md-8">
            {cart.map((item, index) => (
              <div key={index} className="list-group-item d-flex justify-content-between align-items-center">
                <div>
                  <img src={item.imageUrl} alt={item.name} width="50" height="50" />
                  <span>{item.name}</span>
                </div>
                <div>
                  <button onClick={() => updateQuantity(item, item.quantity + 1)}>+</button>
                  <span>{item.quantity}</span>
                  <button onClick={() => updateQuantity(item, item.quantity - 1)}>-</button>
                </div>
                <div>
                  <span>₹{item.price * item.quantity}</span>
                  <button onClick={() => removeFromCart(item)}>Remove</button>
                </div>
              </div>
            ))}
          </div>

          <div className="col-md-4">
            <div className="card">
              <div className="card-body">
                <h4>Order Summary</h4>
                <ul className="list-group list-group-flush">
                  <li className="list-group-item d-flex justify-content-between">
                    <span>Subtotal</span>
                    <span>₹{cart.reduce((sum, item) => sum + item.price * item.quantity, 0)}</span>
                  </li>
                  <li className="list-group-item d-flex justify-content-between">
                    <span>Shipping</span>
                    <span>₹50</span>
                  </li>
                  <li className="list-group-item d-flex justify-content-between">
                    <strong>Total</strong>
                    <strong>₹{cart.reduce((sum, item) => sum + item.price * item.quantity, 0) + 50}</strong>
                  </li>
                </ul>
              </div>
            </div>

            <div className="card mt-3">
              <div className="card-body">
                <h5>Delivery Address</h5>
                <form>
                  <div className="mb-3">
                    <label htmlFor="village" className="form-label">Village</label>
                    <input type="text" className="form-control" name="village" value={address.village} onChange={handleChange} />
                  </div>
                  <div className="mb-3">
                    <label htmlFor="streetNo" className="form-label">Street No</label>
                    <input type="text" className="form-control" name="streetNo" value={address.streetNo} onChange={handleChange} />
                  </div>
                  <div className="mb-3">
                    <label htmlFor="city" className="form-label">City</label>
                    <input type="text" className="form-control" name="city" value={address.city} onChange={handleChange} />
                  </div>
                  <div className="mb-3">
                    <label htmlFor="state" className="form-label">State</label>
                    <input type="text" className="form-control" name="state" value={address.state} onChange={handleChange} />
                  </div>
                  <div className="mb-3">
                    <label htmlFor="pincode" className="form-label">Pincode</label>
                    <input type="text" className="form-control" name="pincode" value={address.pincode} onChange={handleChange} />
                  </div>
                  <button type="button" className="btn btn-dark w-100" onClick={handleSubmit}>Place Order</button>
                </form>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Cart;
