import React, { useState } from 'react';
import './Login.css';

function Login({ setIsLoggedIn, setUserName, setActiveCategory }) {
  const [emailOrMobile, setEmailOrMobile] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ emailOrMobile, password }),
      });
      const data = await response.json();
      if (response.ok) {
        alert(data.msg);
        
        // Extract and capitalize the username
        const name = emailOrMobile.split('@')[0]; // Extract name from email
        const capitalizedName = name.charAt(0).toUpperCase() + name.slice(1).toLowerCase();
        setUserName(capitalizedName); // Set the capitalized name

        setIsLoggedIn(true); // Set user as logged in
        setActiveCategory("home"); // Redirect to home
      } else {
        alert(data.msg);
      }
    } catch (err) {
      console.error('Error:', err);
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <h3 className="login-heading">Log In</h3>
        <form onSubmit={handleLogin}>
          <div className="mb-3">
            <label htmlFor="emailOrMobile" className="form-label login-label">Email or Mobile</label>
            <input
              type="text"
              id="emailOrMobile"
              className="form-control"
              value={emailOrMobile}
              onChange={(e) => setEmailOrMobile(e.target.value)}
              placeholder="Enter email or mobile number"
              required
            />
          </div>
          <div className="mb-3">
            <label htmlFor="password" className="form-label login-label">Password</label>
            <input
              type="password"
              id="password"
              className="form-control"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter password"
              required
            />
          </div>
          <button type="submit" className="btn btn-dark">Log In</button>
        </form>
      </div>
    </div>
  );
}

export default Login;
