import React, { useState, useEffect } from "react";
import mensWearData from './mens.json'; 
import womensWearData from './Mens/womens.json';
import newarrivals from './Mens/newarrivals.json';
import './Mens.css';

const ProductCard = ({ product }) => {
  const handleAddToCart = () => {
    console.log(`${product.name} added to cart`);
  };

  const handleAddToWishlist = () => {
    console.log(`${product.name} added to wishlist`);
  };

  return (
    <div className="card" style={{ width: "18rem", margin: "10px" }}>
      <img src={product.imageUrl} className="card-img-top" alt={product.name} height={300} width={300} />
      <div className="card-body">
        <h5 className="card-title">{product.name}</h5>
        <p className="card-text">Price: â‚¹{product.price}</p>
        <div className="card-actions">
          <button onClick={handleAddToCart} className="btn btn-dark">
            <i className="fas fa-shopping-cart"></i> Add to Cart
          </button>
          <button onClick={handleAddToWishlist} className="btn btn-light">
            <i className="fas fa-heart"></i> Add to Wishlist
          </button>
        </div>
      </div>
    </div>
  );
};

const CategoryCard = ({ category, selectedCategory, setSelectedCategory }) => {
  return (
    <button
      className={`category-btn ${selectedCategory === category.category ? 'active' : ''}`}
      onClick={() => setSelectedCategory(category.category)}
    >
      {category.category}
    </button>
  );
};

const MensWearPage = ({ category }) => {
  const [data, setData] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [sortOrder, setSortOrder] = useState(null); // Null to represent no sort order initially
  const [showSortOptions, setShowSortOptions] = useState(false); // To show/hide the sort options dropdown

  useEffect(() => {
    // Dynamically load the data based on the category (mens, womens, newarrivals)
    if (category === "mens") {
      setData(mensWearData.mensWear);
    } else if (category === "womens") {
      setData(womensWearData.womensWear);
    } else if (category === "newarrivals") {
      setData(newarrivals.newArrivals);  // Make sure to use the correct path for newarrivals
    }
  }, [category]); // Re-run effect when category changes

  // Filter products based on selected category
  const filteredItems = selectedCategory === 'All'
    ? [].concat(...data.map(category => category.items)) // Show all items if 'All' is selected
    : [].concat(...data
        .filter(category => category.category === selectedCategory)
        .map(category => category.items)); // Filter by selected category

  // Sort the filtered items based on price
  const sortedItems = [...filteredItems].sort((a, b) => {
    if (sortOrder === 'asc') return a.price - b.price;
    if (sortOrder === 'desc') return b.price - a.price;
    return 0; // No sorting if sortOrder is null
  });

  // Handle sort option selection
  const handleSortSelect = (order) => {
    setSortOrder(order);
    setShowSortOptions(false); // Close dropdown after selection
  };

  return (
    <div className="container">
      {/* Category buttons */}
      <div className="category-buttons">
        <CategoryCard category={{ category: 'All' }} selectedCategory={selectedCategory} setSelectedCategory={setSelectedCategory} />
        {/* Render the categories based on the loaded data */}
        {data.map((category) => (
          <CategoryCard key={category.id} category={category} selectedCategory={selectedCategory} setSelectedCategory={setSelectedCategory} />
        ))}
      </div>

      {/* Sort Button with Dropdown */}
      <div className="sort-container">
        <button 
          onClick={() => setShowSortOptions(!showSortOptions)} 
          className="sort-button">
          Sort by Price
        </button>

        {/* Dropdown for Sort Options */}
        {showSortOptions && (
          <div className="sort-options-dropdown">
            <button onClick={() => handleSortSelect('asc')} className="sort-option">
              Lowest to Highest
            </button>
            <button onClick={() => handleSortSelect('desc')} className="sort-option">
              Highest to Lowest
            </button>
          </div>
        )}
      </div>

      {/* Display sorted and filtered products */}
      <div className="products">
        {sortedItems.map((item) => (
          <ProductCard key={item.name} product={item} />
        ))}
      </div>
    </div>
  );
};

export default MensWearPage;
