import React, { useContext, useState } from "react";
import { CartContext } from "./CartContext.jsx";
import { useOrder } from "./OrderContext.jsx";
import './cart.css';  // Make sure this import is at the top of the file

const Cart = () => {
  const { cart, updateQuantity, removeFromCart } = useContext(CartContext);
  const { addOrder, orders } = useOrder();  // Make sure orders are also available from the OrderContext

  const [address, setAddress] = useState({
    village: "",
    streetNo: "",
    city: "",
    state: "",
    pincode: "",
  });

  const handleSubmit = () => {
    if (!address.village || !address.city || !address.state || !address.pincode) {
      alert("Please fill in all address details.");
      return;
    }

    const newOrder = {
      items: [...cart],
      total: cart.reduce((total, item) => total + item.price * item.quantity, 0) + 50,
      address,
      orderDate: new Date().toLocaleString(),
    };

    addOrder(newOrder);  // Add the order to the order context
    alert("Order placed successfully!");

    // Reset the address form and cart after placing the order
    setAddress({ village: "", streetNo: "", city: "", state: "", pincode: "" });
    removeFromCart();  // Remove all items from cart after order is placed
  };

  return (
    <div className="cart container mt-5">
      <h1>Your Cart</h1>
      {cart.length === 0 ? (
        <p className="empty-cart">Your cart is empty!</p>
      ) : (
        <div className="cart-container">
          {/* Cart Items */}
          <div className="cart-items">
            {cart.map((item, index) => (
              <div key={index} className="cart-item">
                <img src={item.image} alt={item.name} />
                <div className="item-details">
                  <h4>{item.name}</h4>
                  <p>Price: ₹{item.price} | Quantity: {item.quantity}</p>
                  <div className="quantity-controls">
                    <button onClick={() => updateQuantity(item, item.quantity - 1)}>-</button>
                    <span>{item.quantity}</span>
                    <button onClick={() => updateQuantity(item, item.quantity + 1)}>+</button>
                  </div>
                </div>
                <button
                  className="btn btn-sm btn-danger"
                  onClick={() => removeFromCart(item)}
                >
                  Remove
                </button>
              </div>
            ))}
          </div>

          {/* Cart Summary */}
          <div className="cart-summary">
            <h3>Order Summary</h3>
            <div className="summary-item">
              <span>Total Items:</span>
              <span>{cart.length}</span>
            </div>
            <div className="summary-item">
              <span>Total Price:</span>
              <span>₹{cart.reduce((total, item) => total + item.price * item.quantity, 0)}</span>
            </div>
            <div className="summary-item">
              <span>Shipping:</span>
              <span>₹50</span>
            </div>
            <div className="summary-item">
              <span>Order Total:</span>
              <span>₹{cart.reduce((total, item) => total + item.price * item.quantity, 0) + 50}</span>
            </div>

            {/* Address Form */}
            <h4>Enter Address:</h4>
            <input
              type="text"
              placeholder="Village"
              value={address.village}
              onChange={(e) => setAddress({ ...address, village: e.target.value })}
              className="form-control my-2"
            />
            <input
              type="text"
              placeholder="Street No"
              value={address.streetNo}
              onChange={(e) => setAddress({ ...address, streetNo: e.target.value })}
              className="form-control my-2"
            />
            <input
              type="text"
              placeholder="City"
              value={address.city}
              onChange={(e) => setAddress({ ...address, city: e.target.value })}
              className="form-control my-2"
            />
            <input
              type="text"
              placeholder="State"
              value={address.state}
              onChange={(e) => setAddress({ ...address, state: e.target.value })}
              className="form-control my-2"
            />
            <input
              type="text"
              placeholder="Pincode"
              value={address.pincode}
              onChange={(e) => setAddress({ ...address, pincode: e.target.value })}
              className="form-control my-2"
            />
            <button className="btn btn-success mt-3" onClick={handleSubmit}>
              Place Order
            </button>
          </div>
        </div>
      )}

      {/* Orders List */}
      <div className="orders-list mt-5">
        <h2>Order History</h2>
        {orders.length === 0 ? (
          <p>No orders yet!</p>
        ) : (
          <ul>
            {orders.map((order, index) => (
              <li key={index}>
                <h4>Order placed on: {order.orderDate}</h4>
                <p>Shipping Address: {order.address.village}, {order.address.city}, {order.address.state}, {order.address.pincode}</p>
                <p>Total: ₹{order.total}</p>
                <ul>
                  {order.items.map((item, idx) => (
                    <li key={idx}>
                      {item.name} (x{item.quantity}) - ₹{item.price * item.quantity}
                    </li>
                  ))}
                </ul>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default Cart;
