import React, { useState, useEffect, useContext } from "react";
import mensWearData from './mens.json';
import womensWearData from './womens.json';
import newarrivals from './newarrivals.json';
import './Mens.css';
import { useCart } from '../Cart/CartContext.jsx';

const ProductCard = ({ product, onAddToCart }) => {
  const handleAddToWishlist = () => {
    console.log(`${product.name} added to wishlist`);
  };

  return (
    <div className="card" style={{ width: "18rem", margin: "10px" }}>
      <img src={product.imageUrl} className="card-img-top" alt={product.name} height={300} width={300} />
      <div className="card-body">
        <h5 className="card-title">{product.name}</h5>
        <p className="card-text">Price: â‚¹{product.price}</p>
        <div className="card-actions">
          <button onClick={() => onAddToCart(product)} className="btn btn-dark">
            <i className="fas fa-shopping-cart"></i> Add to Cart
          </button>
          <button onClick={handleAddToWishlist} className="btn btn-light">
            <i className="fas fa-heart"></i> Add to Wishlist
          </button>
        </div>
      </div>
    </div>
  );
};

const CategoryButton = ({ category, isActive, onClick }) => (
  <button
    className={`category-btn ${isActive ? 'active' : ''}`}
    onClick={onClick}
  >
    {category}
  </button>
);

const MensWearPage = ({ category }) => {
  const [data, setData] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [sortOrder, setSortOrder] = useState(null);
  const [cartMessage, setCartMessage] = useState(""); // State for cart message
  const { addToCart } = useContext(CartContext);

  console.log('MensWearPage component rendered');

  useEffect(() => {
    console.log(`Category selected: ${category}`);
    console.log("newarrivals data: ", newarrivals);
  
    switch (category) {
      case "mens":
        setData(mensWearData.mensWear);
        break;
      case "womens":
        setData(womensWearData.womensWear);
        break;
      case "newArrivals":
        if (newarrivals && newarrivals.newArrivals) {
          console.log("Rendering MensWearPage with category:", category);
          console.log("New Arrivals Data: ", newarrivals.newArrivals);
          setData(newarrivals.newArrivals);
        } else {
          console.log("No new arrivals data found");
          setData([]);
        }
        break;
      default:
        setData([]);
    }
  }, [category]);

  const filteredItems = selectedCategory === 'All'
    ? data.flatMap(cat => cat.items || [])
    : data
        .filter(cat => cat.category === selectedCategory)
        .flatMap(cat => cat.items || []);

  const sortedItems = [...filteredItems].sort((a, b) => {
    if (sortOrder === 'asc') return a.price - b.price;
    if (sortOrder === 'desc') return b.price - a.price;
    return 0;
  });

  const handleAddToCart = (product) => {
    console.log('handleAddToCart function called');
    addToCart(product);
    setCartMessage(`${product.name} added to cart`); // Set the cart message

    // Clear the message after 3 seconds
    setTimeout(() => {
      setCartMessage("");
    }, 3000);
  };

  return (
    <div className="container">
      {/* Floating Cart Message */}
      {cartMessage && (
        <div className="floating-message">
          {cartMessage}
        </div>
      )}

      {/* Category Buttons */}
      <div className="category-buttons">
        <CategoryButton
          category="All"
          isActive={selectedCategory === 'All'}
          onClick={() => setSelectedCategory('All')}
        />
        {data.map(cat => (
          <CategoryButton
            key={cat.id}
            category={cat.category}
            isActive={selectedCategory === cat.category}
            onClick={() => setSelectedCategory(cat.category)}
          />
        ))}
      </div>

      {/* Sort Options */}
      <div className="sort-container">
        <button
          onClick={() => setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc')}
          className="sort-button"
        >
          Sort by Price ({sortOrder === 'asc' ? 'Lowest to Highest' : 'Highest to Lowest'})
        </button>
      </div>

      {/* Product Cards */}
      <div className="products">
        {sortedItems.length === 0 ? (
          <p>No products available in this category</p>
        ) : (
          sortedItems.map(product => (
            <ProductCard key={product.name} product={product} onAddToCart={handleAddToCart} />
          ))
        )}
      </div>
    </div>
  );
};

export default MensWearPage;
