import React, { useContext } from "react";
import { CartContext } from '../Cart/CartContext.jsx';
 // Ensure correct import of CartContext


const WishlistPage = () => {
  // Use the correct CartContext or useCart hook
  const { wishlist, removeFromWishlist } = useContext(CartContext); // Correct usage of CartContext

  const handleRemoveFromWishlist = (productId) => {
    removeFromWishlist(productId); // Call remove from wishlist with product id
  };

  return (
    <div className="container">
      <h2>Wishlist</h2>
      <div className="wishlist-products">
        {wishlist.length === 0 ? (
          <p>Your wishlist is empty</p>
        ) : (
          wishlist.map(product => (
            <div key={product.id} className="card" style={{ width: "18rem", margin: "10px" }}>
              <img 
                src={product.imageUrl} 
                className="card-img-top" 
                alt={product.name} 
                height={300} 
                width={300} 
              />
              <div className="card-body">
                <h5 className="card-title">{product.name}</h5>
                <p className="card-text">Price: â‚¹{product.price}</p>
                <button 
                  onClick={() => handleRemoveFromWishlist(product.id)} 
                  className="btn btn-light">
                  <i className="fas fa-heart-broken"></i> Remove from Wishlist
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default WishlistPage;
