import { useContext } from 'react';
import { CartContext } from './CartContext.jsx';
import './cart.css';

const Cart = () => {
  console.log('Cart component rendered');
  const { cart, addToCart, removeFromCart, updateQuantity } = useContext(CartContext);

  console.log('Cart state:', cart);

  // Function to handle increasing the quantity
  const increaseQuantity = (item) => {
    updateQuantity(item, item.quantity + 1); // Increment the quantity
  };

  // Function to handle decreasing the quantity
  const decreaseQuantity = (item) => {
    if (item.quantity > 1) {
      updateQuantity(item, item.quantity - 1); // Decrement the quantity
    } else {
      removeFromCart(item); // Remove item if quantity is 1 and user wants to decrease
    }
  };

  return (
    <div className="cart">
      <h2>Your Cart</h2>
      <div className="cart-container">
        {cart.length === 0 ? (
          <p className="empty-cart">Uh - Oh! Your cart appears to be empty</p>
        ) : (
          <div className="cart-items">
            {cart.map((item, index) => (
              <div key={index} className="cart-item">
                <img src={item.imageUrl} alt={item.name} />
                <div className="item-details">
                  <h3>{item.name}</h3>
                  <p>Price: ₹{item.price}</p>
                  <div className="quantity-controls">
                    <button
                      className="btn btn-dark"
                      onClick={() => {
                        console.log('Add to Cart button clicked');
                        increaseQuantity(item);
                      }}
                    >
                      +
                    </button>
                    <span>{item.quantity}</span>
                    <button
                      className="btn btn-danger"
                      onClick={() => {
                        console.log('Remove from Cart button clicked');
                        decreaseQuantity(item);
                      }}
                    >
                      -
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
        <div className="cart-summary">
          <h3>Order Summary</h3>
          <div className="summary-item">
            <span>Subtotal:</span>
            <span>
              ₹
              {cart.reduce(
                (total, item) => total + item.price * item.quantity,
                0
              )}
            </span>
          </div>
          <div className="summary-item">
            <span>Shipping:</span>
            <span>₹50</span>
          </div>
          <div className="summary-item">
            <span>Total:</span>
            <span>
              ₹
              {cart.reduce(
                (total, item) => total + item.price * item.quantity,
                0
              ) + 50}
            </span>
          </div>
          <button className="btn btn-success">Proceed to Checkout</button>
        </div>
      </div>
    </div>
  );
};

export default Cart;
