import React, { useEffect, useState } from "react";
import axios from "axios";

function Account() {
  const [userDetails, setUserDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [editMode, setEditMode] = useState(false);
  const [formValues, setFormValues] = useState({
    name: "",
    mobile: "",
    profileImage: "",
  });

  useEffect(() => {
    // Get the logged-in user's emailOrMobile from local storage
    const user = JSON.parse(localStorage.getItem("user"));
    const emailOrMobile = user?.emailOrMobile;

    if (!emailOrMobile) {
      alert("User not logged in");
      return;
    }

    // Fetch user details from the server
    axios
      .get(`http://localhost:5000/api/user/${emailOrMobile}`)
      .then((response) => {
        setUserDetails(response.data);
        setFormValues({
          name: response.data.name || "",
          mobile: response.data.mobile || "",
          profileImage: response.data.profileImage || "",
        });
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching user details:", error);
        setLoading(false);
      });
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormValues({ ...formValues, [name]: value });
  };

  const handleUpdateClick = () => {
    setEditMode(true);
  };

  const handleSaveClick = () => {
    const user = JSON.parse(localStorage.getItem("user"));
    const emailOrMobile = user?.emailOrMobile;

    axios
      .put(`http://localhost:5000/api/user/${emailOrMobile}`, formValues)
      .then((response) => {
        setUserDetails(response.data.user);
        setEditMode(false);
      })
      .catch((error) => {
        console.error("Error updating profile:", error);
      });
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  if (!userDetails) {
    return <p>No user details found.</p>;
  }

  return (
    <div>
      <h2>Account Details</h2>
      {editMode ? (
        <div>
          <label>
            Name:
            <input
              type="text"
              name="name"
              value={formValues.name}
              onChange={handleInputChange}
            />
          </label>
          <br />
          <label>
            Mobile:
            <input
              type="text"
              name="mobile"
              value={formValues.mobile}
              onChange={handleInputChange}
            />
          </label>
          <br />
          <label>
            Profile Image URL:
            <input
              type="text"
              name="profileImage"
              value={formValues.profileImage}
              onChange={handleInputChange}
            />
          </label>
          <br />
          <button onClick={handleSaveClick}>Save</button>
        </div>
      ) : (
        <div>
          <img
            src={userDetails.profileImage || "default-profile.png"}
            alt="Profile"
            style={{ width: "100px", borderRadius: "50%" }}
          />
          <p><strong>Name:</strong> {userDetails.name}</p>
          <p><strong>Mobile:</strong> {userDetails.mobile}</p>
          <p><strong>Email:</strong> {userDetails.emailOrMobile}</p>
          <button onClick={handleUpdateClick}>Update Profile</button>
        </div>
      )}
    </div>
  );
}

export default Account;
