import React from "react";
import { useOrder } from "./OrderContext.jsx";

const OrderHistory = () => {
  const { orders } = useOrder();

  return (
    <div className="container mt-5">
      <h2 className="text-center">Order History</h2>
      {orders.length === 0 ? (
        <p className="text-center">No orders placed yet.</p>
      ) : (
        orders.map((order, index) => (
          <div key={index} className="order-item mb-4">
            <h5>Order #{index + 1}</h5>
            <p>Date: {order.orderDate}</p>
            <p>Total: ₹{order.total}</p>
            <p>Address: {`${order.address.village}, ${order.address.streetNo}, ${order.address.city}, ${order.address.state}, ${order.address.pincode}`}</p>
            <ul>
              {order.items.map((item, i) => (
                <li key={i}>
                  {item.name} - {item.quantity} x ₹{item.price} = ₹{item.quantity * item.price}
                </li>
              ))}
            </ul>
          </div>
        ))
      )}
    </div>
  );
};

export default OrderHistory;
