import { useContext, useState } from 'react';
import { CartContext } from './CartContext.jsx';
import './cart.css';

const Cart = () => {
  const { cart, addToCart, removeFromCart, updateQuantity } = useContext(CartContext);

  // State for delivery address
  const [address, setAddress] = useState({
    village: '',
    streetNo: '',
    city: '',
    state: '',
    pincode: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setAddress((prevAddress) => ({
      ...prevAddress,
      [name]: value,
    }));
  };

  const handleSubmit = () => {
    console.log("Delivery Address:", address);
    alert("Order placed successfully!");
  };

  // Function to handle increasing the quantity
  const increaseQuantity = (item) => {
    updateQuantity(item, item.quantity + 1);
  };

  // Function to handle decreasing the quantity
  const decreaseQuantity = (item) => {
    if (item.quantity > 1) {
      updateQuantity(item, item.quantity - 1);
    } else {
      removeFromCart(item);
    }
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">Your Cart</h2>
      {cart.length === 0 ? (
        <div className="d-flex flex-column align-items-center justify-content-center text-center py-5 border rounded">
          <img src="https://th.bing.com/th/id/OIP.GTpAJzc2mjH1AwOQgrda3AHaHa?w=200&h=201&c=7&r=0&o=5&pid=1.7" alt="" />
          <h5 className="mb-3">Uh-Oh! Your cart appears to be empty!</h5>
          <button className="btn btn-dark">Shop Now</button>
        </div>
      ) : (
        <div className="row">
          {/* Cart Items Section */}
          <div className="col-md-8">
            <div className="list-group">
              {cart.map((item, index) => (
                <div
                  key={index}
                  className="list-group-item d-flex align-items-center justify-content-between mb-3 border rounded"
                >
                  <img
                    src={item.imageUrl}
                    alt={item.name}
                    className="img-fluid rounded"
                    style={{ width: '80px', height: '80px', objectFit: 'cover' }}
                  />
                  <div className="flex-grow-1 mx-3">
                    <h5>{item.name}</h5>
                    <p className="mb-1">Price: ₹{item.price}</p>
                    <div className="d-flex align-items-center">
                      <button
                        className="btn btn-dark btn-sm me-2"
                        onClick={() => increaseQuantity(item)}
                      >
                        +
                      </button>
                      <span className="mt-3">{item.quantity}</span>
                      <button
                        className="btn btn-danger btn-sm ms-2"
                        onClick={() => decreaseQuantity(item)}
                      >
                        -
                      </button>
                    </div>
                  </div>
                  <div className="text-end">
                    <p className="fw-bold">₹{item.price * item.quantity}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Order Summary Section */}
          <div className="col-md-4">
            <div className="card border-0 shadow-sm">
              <div className="card-body">
                <h4 className="card-title">Order Summary</h4>
                <ul className="list-group list-group-flush mb-3">
                  <li className="list-group-item d-flex justify-content-between">
                    <span>Subtotal</span>
                    <span>₹{cart.reduce((total, item) => total + item.price * item.quantity, 0)}</span>
                  </li>
                  <li className="list-group-item d-flex justify-content-between">
                    <span>Shipping</span>
                    <span>₹50</span>
                  </li>
                  <li className="list-group-item d-flex justify-content-between fw-bold">
                    <span>Total</span>
                    <span>
                      ₹
                      {cart.reduce((total, item) => total + item.price * item.quantity, 0) + 50}
                    </span>
                  </li>
                </ul>
              </div>
            </div>

            {/* Delivery Address Form */}
            <div className="card mt-4">
              <div className="card-body">
                <h5 className="card-title">Delivery Address</h5>
                <form>
                  <div className="mb-3">
                    <label htmlFor="village" className="form-label">Village</label>
                    <input
                      type="text"
                      className="form-control"
                      id="village"
                      name="village"
                      value={address.village}
                      onChange={handleChange}
                    />
                  </div>
                  <div className="mb-3">
                    <label htmlFor="streetNo" className="form-label">Street No/House No</label>
                    <input
                      type="text"
                      className="form-control"
                      id="streetNo"
                      name="streetNo"
                      value={address.streetNo}
                      onChange={handleChange}
                    />
                  </div>
                  <div className="mb-3">
                    <label htmlFor="city" className="form-label">City</label>
                    <input
                      type="text"
                      className="form-control"
                      id="city"
                      name="city"
                      value={address.city}
                      onChange={handleChange}
                    />
                  </div>
                  <div className="mb-3">
                    <label htmlFor="state" className="form-label">State</label>
                    <input
                      type="text"
                      className="form-control"
                      id="state"
                      name="state"
                      value={address.state}
                      onChange={handleChange}
                    />
                  </div>
                  <div className="mb-3">
                    <label htmlFor="pincode" className="form-label">Pincode</label>
                    <input
                      type="text"
                      className="form-control"
                      id="pincode"
                      name="pincode"
                      value={address.pincode}
                      onChange={handleChange}
                    />
                  </div>
                  <button
                    type="button"
                    className="btn btn-success w-100"
                    onClick={handleSubmit}
                  >
                    Place Order
                  </button>
                </form>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Cart;
