import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Navbar from "./Navbar/Navbar";
import MensWearPage from "./Shop/Products.jsx";
import Hero from "./HeroSection/Hero.jsx";
import Collections from "./Collections/collections.jsx";
import About from "./About/about.jsx";
import Contact from "./Contact/contact.jsx";
import Cart from "./Cart/cart.jsx";
import Orders from "./Cart/orders.jsx";
import CartProvider from "./Cart/CartContext.jsx";
import Login from "./UserAuth/Login.jsx";
import Signup from "./UserAuth/Signup.jsx";
import "bootstrap/dist/css/bootstrap.min.css";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Track login status
  const [userEmailOrMobile, setUserEmailOrMobile] = useState(""); // Track logged-in user

  return (
    <CartProvider>
      <Router>
        <div className="App">
          <Navbar
            setActiveCategory={() => {}}
            isLoggedIn={isLoggedIn}
            setIsLoggedIn={setIsLoggedIn}
            userName={userEmailOrMobile}
          />
          <Routes>
            {/* Public Routes */}
            <Route path="/" element={<Hero />} />
            <Route path="/collections" element={<Collections />} />
            <Route path="/about" element={<About />} />
            <Route path="/contact" element={<Contact />} />
            <Route path="/mens" element={<MensWearPage category="mens" />} />
            <Route path="/womens" element={<MensWearPage category="womens" />} />
            <Route path="/new-arrivals" element={<MensWearPage category="newArrivals" />} />

            {/* Authentication */}
            <Route
              path="/login"
              element={
                <Login
                  setIsLoggedIn={setIsLoggedIn}
                  setUserName={setUserEmailOrMobile}
                />
              }
            />
            <Route path="/signup" element={<Signup />} />

            {/* Protected Routes */}
            <Route
              path="/cart"
              element={isLoggedIn ? <Cart emailOrMobile={userEmailOrMobile} /> : <Navigate to="/login" />}
            />
            <Route
              path="/orders"
              element={isLoggedIn ? <Orders /> : <Navigate to="/login" />}
            />

            {/* Fallback Route */}
            <Route path="*" element={<Navigate to="/" />} />
          </Routes>
        </div>
      </Router>
    </CartProvider>
  );
}

export default App;
