import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Mens.css"; // Assuming you have styles for the component

const MensWearPage = ({ category, userEmailOrMobile }) => {
  const [products, setProducts] = useState([]);
  const [cartMessage, setCartMessage] = useState("");

  // Fetch products on category change
  useEffect(() => {
    axios
      .get(`/api/products?category=${category}`)
      .then((response) => setProducts(response.data))
      .catch((error) => console.error("Error fetching products:", error));
  }, [category]);

  // Handle adding product to cart
  const handleAddToCart = async (product) => {
    if (!userEmailOrMobile) {
      setCartMessage("You need to log in to add items to the cart.");
      return;
    }

    try {
      const response = await axios.post("/api/cart", {
        emailOrMobile: userEmailOrMobile,
        product,
      });

      setCartMessage(response.data.message || "Added to cart!");
      setTimeout(() => setCartMessage(""), 3000); // Clear message after 3 seconds
    } catch (error) {
      console.error("Error adding to cart:", error);
      setCartMessage("Failed to add item to the cart.");
    }
  };

  return (
    <div className="menswear-container">
      <h2>{category.charAt(0).toUpperCase() + category.slice(1)} Products</h2>
      {cartMessage && <p className="cart-message">{cartMessage}</p>}
      <div className="product-grid">
        {products.length ? (
          products.map((product) => (
            <div key={product.id} className="product-card">
              <img src={product.imageUrl} alt={product.name} className="product-image" />
              <h3>{product.name}</h3>
              <p>Price: â‚¹{product.price}</p>
              <button
                className="add-to-cart-button"
                onClick={() => handleAddToCart(product)}
              >
                Add to Cart
              </button>
            </div>
          ))
        ) : (
          <p>No products available for this category.</p>
        )}
      </div>
    </div>
  );
};

export default MensWearPage;
