// CartContext.jsx
import React, { createContext, useState, useContext } from "react";

const CartContext = createContext();

export const CartProvider = ({ children }) => {
  const [cart, setCart] = useState([]);
  const [wishlist, setWishlist] = useState([]);

  const addToCart = (product) => setCart((prevCart) => [...prevCart, product]);
  const removeFromCart = (productId) => setCart((prevCart) => prevCart.filter((item) => item.id !== productId));
  const addToWishlist = (product) => setWishlist((prevWishlist) => [...prevWishlist, product]);
  const removeFromWishlist = (productId) => setWishlist((prevWishlist) => prevWishlist.filter((item) => item.id !== productId));

  const isProductInWishlist = (productId) => wishlist.some((product) => product.id === productId);

  return (
    <CartContext.Provider value={{
      cart, wishlist, addToCart, removeFromCart, addToWishlist, removeFromWishlist, isProductInWishlist
    }}>
      {children}
    </CartContext.Provider>
  );
};

export const useCart = () => useContext(CartContext);
