import React, { useState } from "react";
import Navbar from "./Navbar/Navbar";
import MensWearPage from "./Shop/Products.jsx";
import Hero from "./HeroSection/Hero.jsx";
import Collections from "./Collections/collections.jsx";
import About from "./About/about.jsx";
import Contact from "./Contact/contact.jsx";
import Cart from "./Cart/cart.jsx";
import CartProvider from "./Cart/CartContext.jsx";
import Login from "./UserAuth/Login.jsx";
import Signup from "./UserAuth/Signup.jsx";
import "bootstrap/dist/css/bootstrap.min.css";

function App() {
  const [activeCategory, setActiveCategory] = useState("home");
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Track login status
  const [userEmailOrMobile, setUserEmailOrMobile] = useState(""); // Track logged-in user

  const handleCategorySelect = (category) => {
    setActiveCategory(category);
  };

  return (
    <CartProvider>
      <div className="App">
        <Navbar
          setActiveCategory={handleCategorySelect}
          isLoggedIn={isLoggedIn}
          setIsLoggedIn={setIsLoggedIn}
          userName={userEmailOrMobile}
        />
        {/* Dynamic Page Rendering */}
        {activeCategory === "collections" && <Collections />}
        {activeCategory === "about" && <About />}
        {activeCategory === "contact" && <Contact />}
        {activeCategory === "home" && <Hero />}
        {/* Ensure products display properly */}
        {(activeCategory === "mens" || activeCategory === "womens" || activeCategory === "newarrivals") && (
          <MensWearPage category={activeCategory} />
        )}
        {/* Cart only visible when logged in */}
        {activeCategory === "cart" && isLoggedIn && <Cart emailOrMobile={userEmailOrMobile} />}
        {activeCategory === "Login" && (
          <Login
            setIsLoggedIn={setIsLoggedIn}
            setUserName={setUserEmailOrMobile}
            setActiveCategory={setActiveCategory}
          />
        )}
        {activeCategory === "Signup" && <Signup />}
      </div>
    </CartProvider>
  );
}

export default App;
