import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./Account.css";

function Account() {
  const [userDetails, setUserDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [editMode, setEditMode] = useState(false);
  const [formValues, setFormValues] = useState({
    name: "",
    mobile: "",
    profileImage: "",
    addressLine1: "",
    addressLine2: "",
    city: "",
    state: "",
    postalCode: "",
    country: "",
  });

  const navigate = useNavigate();

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user"));
    const emailOrMobile = user?.emailOrMobile;

    if (!emailOrMobile) {
      alert("User not logged in");
      return;
    }

    axios
      .get(`http://localhost:5000/api/user/${emailOrMobile}`)
      .then((response) => {
        setUserDetails(response.data);
        setFormValues({
          name: response.data.name || "",
          mobile: response.data.mobile || "",
          profileImage: response.data.profileImage || "",
          addressLine1: response.data.addressLine1 || "",
          addressLine2: response.data.addressLine2 || "",
          city: response.data.city || "",
          state: response.data.state || "",
          postalCode: response.data.postalCode || "",
          country: response.data.country || "",
        });
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching user details:", error);
        setLoading(false);
      });
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("user");
    navigate("/");
  };

  const handleProfileChange = (e) => {
    const { name, value } = e.target;
    setFormValues((prevValues) => ({
      ...prevValues,
      [name]: value,
    }));
  };

  const handleProfileUpdate = () => {
    const emailOrMobile = userDetails?.emailOrMobile;
    axios
      .put(`http://localhost:5000/api/user/${emailOrMobile}`, formValues)
      .then((response) => {
        setUserDetails(response.data);
        setEditMode(false);
        alert("Profile updated successfully!");
      })
      .catch((error) => {
        console.error("Error updating profile:", error);
      });
  };

  const nameFromEmail = userDetails?.emailOrMobile?.split("@")[0];

  if (loading) {
    return <p>Loading...</p>;
  }

  if (!userDetails) {
    return <p>No user details found.</p>;
  }

  return (
    <div className="account-container">
      <h2>Account Details</h2>
      <div className="profile-card">
        <div className="profile-details">
          <img
            src={userDetails.profileImage || "default-profile.png"}
            alt="Profile"
          />
          <div>
            {editMode ? (
              <div>
                <input
                  type="text"
                  name="name"
                  value={formValues.name}
                  onChange={handleProfileChange}
                  placeholder="Enter your name"
                  className="form-control"
                />
                <input
                  type="text"
                  name="mobile"
                  value={formValues.mobile}
                  onChange={handleProfileChange}
                  placeholder="Enter your mobile"
                  className="form-control"
                />
                <input
                  type="text"
                  name="profileImage"
                  value={formValues.profileImage}
                  onChange={handleProfileChange}
                  placeholder="Enter profile image URL"
                  className="form-control"
                />
                <input
                  type="text"
                  name="addressLine1"
                  value={formValues.addressLine1}
                  onChange={handleProfileChange}
                  placeholder="Enter address line 1"
                  className="form-control"
                />
                <input
                  type="text"
                  name="addressLine2"
                  value={formValues.addressLine2}
                  onChange={handleProfileChange}
                  placeholder="Enter address line 2"
                  className="form-control"
                />
                <input
                  type="text"
                  name="city"
                  value={formValues.city}
                  onChange={handleProfileChange}
                  placeholder="Enter city"
                  className="form-control"
                />
                <input
                  type="text"
                  name="state"
                  value={formValues.state}
                  onChange={handleProfileChange}
                  placeholder="Enter state"
                  className="form-control"
                />
                <input
                  type="text"
                  name="postalCode"
                  value={formValues.postalCode}
                  onChange={handleProfileChange}
                  placeholder="Enter postal code"
                  className="form-control"
                />
                <input
                  type="text"
                  name="country"
                  value={formValues.country}
                  onChange={handleProfileChange}
                  placeholder="Enter country"
                  className="form-control"
                />
                <button
                  className="btn btn-primary"
                  onClick={handleProfileUpdate}
                >
                  Save Changes
                </button>
              </div>
            ) : (
              <div>
                <p><strong>Name:</strong> {nameFromEmail}</p>
                <p><strong>Mobile:</strong> {userDetails.mobile}</p>
                <p><strong>Email:</strong> {userDetails.emailOrMobile}</p>
                <p><strong>Address:</strong> {userDetails.addressLine1}, {userDetails.addressLine2}, {userDetails.city}, {userDetails.state}, {userDetails.postalCode}, {userDetails.country}</p>
                <button
                  className="btn btn-secondary"
                  onClick={() => setEditMode(true)}
                >
                  Edit Profile
                </button>
              </div>
            )}
          </div>
        </div>
      </div>

      <div className="actions-card">
        <h5 className="card-title">Actions</h5>
        <ul>
          <li>
            <button
              className="btn btn-link"
              onClick={() => navigate("/cart")}
            >
              Your Cart
            </button>
          </li>
          <li>
            <button
              className="btn btn-link"
              onClick={() => navigate("/orders")}
            >
              Your Orders
            </button>
          </li>
          <li>
            <button
              className="btn btn-link"
              onClick={() => navigate("/offers")}
            >
              Offers
            </button>
          </li>
          <li>
            <button
              className="btn btn-danger"
              onClick={handleLogout}
            >
              Logout
            </button>
          </li>
        </ul>
      </div>
    </div>
  );
}

export default Account;
