import React, { useState } from 'react';
import Hero from './HeroSection/Hero.jsx';
import Navbar from './Navbar/Navbar.jsx';
import Mens from './Shop/Mens/Mens.jsx';

function App() {
  const [activeCategory, setActiveCategory] = useState("home");

  const handleCategorySelect = (category) => {
    setActiveCategory(category);
  };

  return (
    <div className="App">
      <Navbar setActiveCategory={handleCategorySelect} />
      {activeCategory === "home" && <Hero />}
      {activeCategory === "mens" && <Mens show mens/>}
      {activeCategory === "womens" && <Mens show womens json />}
      {activeCategory === "newArrivals" && <div>New Arrivals Section</div>}
    </div>
  );
}

export default App;
