import React, { useEffect, useState } from "react";
import axios from "axios";

function Account() {
  const [userDetails, setUserDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [editMode, setEditMode] = useState(false);
  const [formValues, setFormValues] = useState({
    name: "",
    mobile: "",
    profileImage: "",
  });

  const emailOrMobile = "user@example.com"; // Replace this with the logged-in user's identifier

  useEffect(() => {
    // Fetch user details from the database
    axios
      .get(`http://localhost:5000/api/user/${emailOrMobile}`)
      .then((response) => {
        const userData = response.data;
        setUserDetails(userData);
        setFormValues({
          name: userData.name || "",
          mobile: userData.mobile || "",
          profileImage: userData.profileImage || "",
        });
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching user details:", error);
        setLoading(false);
      });
  }, [emailOrMobile]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormValues({ ...formValues, [name]: value });
  };

  const handleUpdateClick = () => {
    setEditMode(true);
  };

  const handleSaveClick = () => {
    // Update user details in the database
    axios
      .put(`http://localhost:5000/api/user/${emailOrMobile}`, formValues)
      .then((response) => {
        setUserDetails(response.data.user);
        setEditMode(false);
      })
      .catch((error) => {
        console.error("Error updating profile:", error);
      });
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  if (!userDetails) {
    return <p>No user details found.</p>;
  }

  return (
    <div>
      <h2>Account Details</h2>
      {editMode ? (
        <div>
          <label>
            Name:
            <input
              type="text"
              name="name"
              value={formValues.name}
              onChange={handleInputChange}
              placeholder="Enter your name"
            />
          </label>
          <br />
          <label>
            Mobile:
            <input
              type="text"
              name="mobile"
              value={formValues.mobile}
              onChange={handleInputChange}
              placeholder="Enter your mobile number"
            />
          </label>
          <br />
          <label>
            Profile Image URL:
            <input
              type="text"
              name="profileImage"
              value={formValues.profileImage}
              onChange={handleInputChange}
              placeholder="Enter profile image URL"
            />
          </label>
          <br />
          <button onClick={handleSaveClick}>Save</button>
        </div>
      ) : (
        <div>
          <img
            src={userDetails.profileImage || "default-profile.png"}
            alt="Profile"
            style={{ width: "100px", borderRadius: "50%" }}
          />
          <p><strong>Name:</strong> {userDetails.name || "Not provided"}</p>
          <p><strong>Mobile:</strong> {userDetails.mobile || "Not provided"}</p>
          <p><strong>Email:</strong> {userDetails.email || "Not provided"}</p>
          <button onClick={handleUpdateClick}>Update Profile</button>
        </div>
      )}
    </div>
  );
}

export default Account;
