import React, { useState, useEffect } from "react";
import mensWearData from './mens.json'; // Adjust path if necessary
import './Mens.css';

const ProductCard = ({ product }) => {
  const handleAddToCart = () => {
    console.log(`${product.name} added to cart`);
  };

  const handleAddToWishlist = () => {
    console.log(`${product.name} added to wishlist`);
  };

  return (
    <div className="card" style={{ width: "18rem", margin: "10px" }}>
      <img src={product.imageUrl} className="card-img-top" alt={product.name} />
      <div className="card-body">
        <h5 className="card-title">{product.name}</h5>
        <p className="card-text">Price: â‚¹{product.price}</p>
        <div className="card-actions">
          {/* Add to Cart and Wishlist Icons */}
          <button onClick={handleAddToCart} className="btn btn-primary">
            <i className="fas fa-shopping-cart"></i> Add to Cart
          </button>
          <button onClick={handleAddToWishlist} className="btn btn-secondary">
            <i className="fas fa-heart"></i> Add to Wishlist
          </button>
        </div>
      </div>
    </div>
  );
};

const CategoryCard = ({ category }) => {
  return (
    <div className="category">
      <h3>{category.category}</h3>
      <p>{category.description}</p>
      <div className="car">
        {category.items.map((item) => (
          <ProductCard key={item.name} product={item} />
        ))}
      </div>
    </div>
  );
};

const MensWearPage = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    // Load data from the JSON file
    setData(mensWearData.mensWear);
  }, []);

  return (
    <div className="container">
      {data.map((category) => (
        <CategoryCard key={category.id} category={category} />
      ))}
    </div>
  );
};

export default MensWearPage;
