import React, { useState } from 'react';
import './Mens.css'; // Ensure this path is correct

const Mens = ({ items }) => {
    const [selectedCategory, setSelectedCategory] = useState('All');

    const handleCategoryClick = (category) => {
        setSelectedCategory(category);
    };

    const filteredItems = selectedCategory === 'All' 
        ? items 
        : items.filter(item => item.category === selectedCategory);

    return (
        <div>
            {/* Category Buttons */}
            <div className="category-buttons">
                <button
                    className={`category-btn ${selectedCategory === 'All' ? 'active' : ''}`}
                    onClick={() => handleCategoryClick('All')}
                >
                    All
                </button>
                <button
                    className={`category-btn ${selectedCategory === 'Dhotis' ? 'active' : ''}`}
                    onClick={() => handleCategoryClick('Dhotis')}
                >
                    Dhotis
                </button>
                <button
                    className={`category-btn ${selectedCategory === 'Shirting Fabrics' ? 'active' : ''}`}
                    onClick={() => handleCategoryClick('Shirting Fabrics')}
                >
                    Shirting Fabrics
                </button>
                <button
                    className={`category-btn ${selectedCategory === 'Suiting Fabrics' ? 'active' : ''}`}
                    onClick={() => handleCategoryClick('Suiting Fabrics')}
                >
                    Suiting Fabrics
                </button>
                <button
                    className={`category-btn ${selectedCategory === 'Kurta Fabrics' ? 'active' : ''}`}
                    onClick={() => handleCategoryClick('Kurta Fabrics')}
                >
                    Kurta Fabrics
                </button>
                <button
                    className={`category-btn ${selectedCategory === 'Lungi' ? 'active' : ''}`}
                    onClick={() => handleCategoryClick('Lungi')}
                >
                    Lungi
                </button>
                <button
                    className={`category-btn ${selectedCategory === 'Sherwani Fabrics' ? 'active' : ''}`}
                    onClick={() => handleCategoryClick('Sherwani Fabrics')}
                >
                    Sherwani Fabrics
                </button>
            </div>

            {/* Products Grid */}
            <div className="products">
                {filteredItems.map((item, index) => (
                    <div className="product-card" key={index}>
                        <img src={item.image} alt={item.name} className="product-image" />
                        <h3>{item.name}</h3>
                        <p>Price: â‚¹{item.price}</p>
                        <div className="card-actions">
                            <button className="btn btn-primary">
                                <i className="fas fa-shopping-cart"></i> Add to Cart
                            </button>
                            <button className="btn btn-secondary">
                                <i className="fas fa-heart"></i> Wishlist
                            </button>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Mens;
