import { useWishlist } from "./wishlistContext.jsx";
import { useHistory } from "react-router-dom"; // Import useHistory hook for navigation
import "./wish.css";
const Wishlist = () => {
  const { wishlist, removeFromWishlist } = useWishlist();
  const history = useHistory(); // Access the history for navigation

  // Function to navigate to the shop page
 

  return (
    <div className="container">
      <h2>Your Wishlist</h2>
      {wishlist.length === 0 ? (
        <div className="empty-wishlist-message text-center">
          <h5>Your wishlist is empty.</h5>
          <p>Check out our collections and add your favorite items to your wishlist!</p>
          <button onClick={goToShop} className="btn btn-primary mt-3">
            Shop Now
          </button>
        </div>
      ) : (
        <div className="row">
          {wishlist.map((item) => (
            <div className="col-md-3" key={item.id}>
              <div className="card" style={{ width: "18rem", margin: "10px" }}>
                <img src={item.imageUrl} className="card-img-top" alt={item.name} height={200} />
                <div className="card-body">
                  <h5 className="card-title">{item.name}</h5>
                  <p className="card-text">Price: â‚¹{item.price}</p>
                  <button 
                    onClick={() => removeFromWishlist(item.id)} 
                    className="btn btn-danger"
                  >
                    Remove from Wishlist
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Wishlist;
