import React, { useState } from "react";
import Navbar from './Navbar/Navbar';
import MensWearPage from './Shop/Mens.jsx'; // Reuse this component for both men's and women's data
import Hero from './HeroSection/Hero.jsx';
import Collections from './collections/collections.jsx';
import About from "./About/about.jsx";
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [activeCategory, setActiveCategory] = useState("home");

  const handleCategorySelect = (category) => {
    setActiveCategory(category);
  };

  return (
    <div className="App">
      <Navbar setActiveCategory={handleCategorySelect} />
      {activeCategory === "collections" && <Collections />}
      {activeCategory === "about" && <About />}
      {activeCategory === "contact" && <Contact />} {/* Ensure this matches the category name */}
      {activeCategory === "home" && <Hero />}
      {activeCategory === "mens" && <MensWearPage category="mens" />}
      {activeCategory === "womens" && <MensWearPage category="womens" />}
      {activeCategory === "newArrivals" && <MensWearPage category="newarrivals" />}
    </div>
  );
}

export default App;
