import React, { useState } from "react";
import Navbar from './Navbar/Navbar';
import MensWearPage from './Shop/Mens.jsx';
import Hero from './HeroSection/Hero.jsx';
import Collections from './Collections/collections.jsx';
import About from "./About/about.jsx";
import Cart from './Cart/cart.jsx';
import CartProvider from "./Cart/CartContext.jsx";
import Login from './UserAuth/Login.jsx';
import Signup from './UserAuth/Signup.jsx';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [activeCategory, setActiveCategory] = useState("home");
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Track login status
  const [userName, setUserName] = useState(""); // Track logged-in user's name

  const handleCategorySelect = (category) => {
    setActiveCategory(category);
  };

  return (
    <CartProvider> 
      <div className="App">
        <Navbar 
          setActiveCategory={handleCategorySelect} 
          isLoggedIn={isLoggedIn} 
          setIsLoggedIn={setIsLoggedIn} 
          userName={userName} // Pass the name to Navbar
        />
        {activeCategory === "collections" && <Collections />}
        {activeCategory === "about" && <About />}
        {activeCategory === "home" && <Hero />}
        {activeCategory === "mens" && <MensWearPage category="mens" />}
        {activeCategory === "womens" && <MensWearPage category="womens" />}
        {activeCategory === "newArrivals" && <MensWearPage category="newarrivals" />}
        {activeCategory === "cart" && isLoggedIn && <Cart />}
        {activeCategory === "Login" && <Login setIsLoggedIn={setIsLoggedIn} setUserName={setUserName} setActiveCategory={setActiveCategory} />}
        {activeCategory === "Signup" && <Signup />}
      </div>
    </CartProvider>
  );
}

export default App;
