import { useContext, useState } from 'react';
import { CartContext } from './CartContext.jsx';
import { useOrder } from './OrderContext';

const Cart = () => {
  const { cart, updateQuantity, removeFromCart } = useContext(CartContext);
  const { addOrder } = useOrder(); // Access order context

  const [address, setAddress] = useState({
    village: '',
    streetNo: '',
    city: '',
    state: '',
    pincode: '',
  });

  const handleSubmit = () => {
    if (!address.village || !address.city || !address.state || !address.pincode) {
      alert("Please fill in all address details.");
      return;
    }

    // Create a new order object
    const newOrder = {
      items: [...cart],
      total: cart.reduce((total, item) => total + item.price * item.quantity, 0) + 50,
      address,
      orderDate: new Date().toLocaleString(),
    };

    addOrder(newOrder); // Save the order
    alert("Order placed successfully!");
    setAddress({ village: '', streetNo: '', city: '', state: '', pincode: '' }); // Reset address
  };
};
