import React, { useContext, useState } from "react";
import { CartContext } from "./CartContext.jsx";
import { useOrder } from "./OrderContext.jsx";

const Cart = () => {
  const { cart, updateQuantity, removeFromCart } = useContext(CartContext);
  const { addOrder } = useOrder();

  const [address, setAddress] = useState({
    village: "",
    streetNo: "",
    city: "",
    state: "",
    pincode: "",
  });

  const handleSubmit = () => {
    if (!address.village || !address.city || !address.state || !address.pincode) {
      alert("Please fill in all address details.");
      return;
    }

    const newOrder = {
      items: [...cart],
      total: cart.reduce((total, item) => total + item.price * item.quantity, 0) + 50,
      address,
      orderDate: new Date().toLocaleString(),
    };

    addOrder(newOrder);
    alert("Order placed successfully!");
    setAddress({ village: "", streetNo: "", city: "", state: "", pincode: "" });
  };

  return (
    <div className="container mt-5">
      <h1>Your Cart</h1>
      {cart.length === 0 ? (
        <p>Your cart is empty!</p>
      ) : (
        <div className="row">
          {/* Order Summary Section */}
          <div className="col-md-4">
            <h4>Order Summary</h4>
            <ul className="list-group">
              {cart.map((item, index) => (
                <li key={index} className="list-group-item d-flex justify-content-between align-items-center">
                  <div>
                    <h5>{item.name}</h5>
                    <p>Price: ₹{item.price} | Quantity: {item.quantity}</p>
                  </div>
                  <div>
                    <button
                      className="btn btn-sm btn-danger me-2"
                      onClick={() => removeFromCart(item)}
                    >
                      Remove
                    </button>
                    <div className="d-flex">
                      <button
                        className="btn btn-sm btn-secondary me-2"
                        onClick={() => updateQuantity(item, item.quantity - 1)}
                      >
                        -
                      </button>
                      <button
                        className="btn btn-sm btn-secondary"
                        onClick={() => updateQuantity(item, item.quantity + 1)}
                      >
                        +
                      </button>
                    </div>
                  </div>
                </li>
              ))}
            </ul>
            <div className="mt-4">
              <h4>Enter Address:</h4>
              <input
                type="text"
                placeholder="Village"
                value={address.village}
                onChange={(e) => setAddress({ ...address, village: e.target.value })}
                className="form-control my-2"
              />
              <input
                type="text"
                placeholder="Street No"
                value={address.streetNo}
                onChange={(e) => setAddress({ ...address, streetNo: e.target.value })}
                className="form-control my-2"
              />
              <input
                type="text"
                placeholder="City"
                value={address.city}
                onChange={(e) => setAddress({ ...address, city: e.target.value })}
                className="form-control my-2"
              />
              <input
                type="text"
                placeholder="State"
                value={address.state}
                onChange={(e) => setAddress({ ...address, state: e.target.value })}
                className="form-control my-2"
              />
              <input
                type="text"
                placeholder="Pincode"
                value={address.pincode}
                onChange={(e) => setAddress({ ...address, pincode: e.target.value })}
                className="form-control my-2"
              />
              <button className="btn btn-success mt-3" onClick={handleSubmit}>
                Place Order
              </button>
            </div>
          </div>

          {/* Cart Items Section */}
          <div className="col-md-8">
            <ul className="list-group">
              {cart.map((item, index) => (
                <li key={index} className="list-group-item d-flex justify-content-between align-items-center">
                  <div className="d-flex align-items-center">
                    <img src={item.image} alt={item.name} className="cart-item-img" />
                    <div className="ms-3">
                      <h5>{item.name}</h5>
                      <p>₹{item.price} | Quantity: {item.quantity}</p>
                    </div>
                  </div>
                  <div>
                    <button
                      className="btn btn-sm btn-danger me-2"
                      onClick={() => removeFromCart(item)}
                    >
                      Remove
                    </button>
                    <div className="d-flex">
                      <button
                        className="btn btn-sm btn-secondary me-2"
                        onClick={() => updateQuantity(item, item.quantity - 1)}
                      >
                        -
                      </button>
                      <button
                        className="btn btn-sm btn-secondary"
                        onClick={() => updateQuantity(item, item.quantity + 1)}
                      >
                        +
                      </button>
                    </div>
                  </div>
                </li>
              ))}
            </ul>
          </div>
        </div>
      )}
    </div>
  );
};

export default Cart;
