import React, { useState, useEffect } from 'react';
import mensWearData from './mens.json';
import womensWearData from './womens.json';
import newarrivals from './newarrivals.json';
import useCart from '../Cart/useCart';
import './Mens.css';

const ProductCard = ({ product }) => {
  const { addToCart } = useCart();

  return (
    <div className="card" style={{ width: "18rem", margin: "10px" }}>
      <img src={product.imageUrl} className="card-img-top" alt={product.name} height={300} width={300} />
      <div className="card-body">
        <h5 className="card-title">{product.name}</h5>
        <p className="card-text">Price: ₹{product.price}</p>
        <div className="card-actions">
          <button onClick={() => addToCart(product)} className="btn btn-dark">
            <i className="fas fa-shopping-cart"></i> Add to Cart
          </button>
          <button className="btn btn-light">
            <i className="fas fa-heart"></i> Add to Wishlist
          </button>
        </div>
      </div>
    </div>
  );
};

const MensWearPage = ({ category }) => {
  const [mensWearData, setMensWearData] = useState([]);
  const [womensWearData, setWomensWearData] = useState([]);
  const [newarrivals, setNewArrivals] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [sortOrder, setSortOrder] = useState(null);
  const { cart } = useCart();

  useEffect(() => {
    // Fetch data based on the category
    if (category === 'mens') {
      setMensWearData(mensWearData);
    } else if (category === 'womens') {
      setWomensWearData(womensWearData);
    } else if (category === 'newarrivals') {
      setNewArrivals(newarrivals);
    }
  }, [category]);

  const filteredItems = selectedCategory === 'All'
    ? [...mensWearData.flatMap(cat => cat.items), ...womensWearData.flatMap(cat => cat.items), ...newarrivals.flatMap(cat => cat.items)]
    : [...mensWearData.filter(cat => cat.category === selectedCategory).flatMap(cat => cat.items), ...womensWearData.filter(cat => cat.category === selectedCategory).flatMap(cat => cat.items), ...newarrivals.filter(cat => cat.category === selectedCategory).flatMap(cat => cat.items)];

  const sortedItems = [...filteredItems].sort((a, b) => {
    if (sortOrder === 'asc') return a.price - b.price;
    if (sortOrder === 'desc') return b.price - a.price;
    return 0;
  });

  return (
    <div className="container">
      <div className="category-buttons">
        <button
          className={`btn btn-${selectedCategory === 'All' ? 'primary' : 'secondary'}`}
          onClick={() => setSelectedCategory('All')}
        >
          All
        </button>
        <button
          className={`btn btn-${selectedCategory === 'Shirts' ? 'primary' : 'secondary'}`}
          onClick={() => setSelectedCategory('Shirts')}
        >
          Shirts
        </button>
        <button
          className={`btn btn-${selectedCategory === 'Pants' ? 'primary' : 'secondary'}`}
          onClick={() => setSelectedCategory('Pants')}
        >
          Pants
        </button>
        <button
          className={`btn btn-${selectedCategory === 'Jackets' ? 'primary' : 'secondary'}`}
          onClick={() => setSelectedCategory('Jackets')}
        >
          Jackets
        </button>
      </div>

      <div className="sort-options">
        <label htmlFor="sort-order">Sort by:</label>
        <select
          id="sort-order"
          value={sortOrder || ''}
          onChange={(e) => setSortOrder(e.target.value)}
        >
          <option value="">None</option>
          <option value="asc">Price: Low to High</option>
          <option value="desc">Price: High to Low</option>
        </select>
      </div>

      <div className="products">
        {sortedItems.map(product => (
          <ProductCard key={product.name} product={product} />
        ))}
      </div>

      <div className="cart-section">
        <h2>Cart</h2>
        {cart.length === 0 ? (
          <p>Your cart is empty.</p>
        ) : (
          <ul>
            {cart.map((item, index) => (
              <li key={index}>{item.name} - ₹{item.price}</li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default MensWearPage;
