import React, { useState, useEffect } from "react";
import mensWearData from './mens.json';
import womensWearData from './womens.json';
import newArrivalsData from './newarrivals.json';
import Cart from '../Cart/cart.jsx';

const ProductCard = ({ product, onAddToCart }) => {
  const handleAddToWishlist = () => {
    console.log(`${product.name} added to wishlist`);
  };

  return (
    <div className="card" style={{ width: "18rem", margin: "10px" }}>
      <img src={product.imageUrl} className="card-img-top" alt={product.name} height={300} width={300} />
      <div className="card-body">
        <h5 className="card-title">{product.name}</h5>
        <p className="card-text">Price: â‚¹{product.price}</p>
        <div className="card-actions">
          <button onClick={() => onAddToCart(product)} className="btn btn-dark">
            <i className="fas fa-shopping-cart"></i> Add to Cart
          </button>
          <button onClick={handleAddToWishlist} className="btn btn-light">
            <i className="fas fa-heart"></i> Add to Wishlist
          </button>
        </div>
      </div>
    </div>
  );
};

const MensWearPage = ({ category }) => {
  const [data, setData] = useState([]);
  const [cart, setCart] = useState([]);

  useEffect(() => {
    console.log("Category selected:", category);
    switch (category) {
      case "mens":
        setData(mensWearData.mensWear);
        break;
      case "womens":
        setData(womensWearData.womensWear);
        break;
      case "newarrivals":
        setData(newArrivalsData.newArrivals);
        break;
      default:
        setData([]);
    }
  }, [category]);

  const handleAddToCart = (product) => {
    setCart((prevCart) => {
      const updatedCart = [...prevCart, product];
      console.log("Updated Cart:", updatedCart);
      return updatedCart;
    });
  };

  return (
    <div className="container">
      <h1>MensWear Page</h1>
      <div className="products">
        {data.length > 0 ? (
          data.flatMap(cat => cat.items).map((product) => (
            <ProductCard key={product.name} product={product} onAddToCart={handleAddToCart} />
          ))
        ) : (
          <p>No products found.</p>
        )}
      </div>
      {/* Render Cart Component */}
      <Cart cart={cart} />
    </div>
  );
};

export default MensWearPage;
