import { useOrder } from "./OrderContext.jsx";

const OrderHistory = () => {
  const { orders } = useOrder();

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">Order History</h2>
      {orders.length === 0 ? (
        <div className="d-flex flex-column align-items-center justify-content-center text-center py-5 border rounded">
          <img
            src="https://th.bing.com/th/id/OIP.mzGICArav_-PODU_Ccd4GQHaGk?w=201&h=200&c=7&r=0&o=5&pid=1.7"
            alt="No Orders"
            className="mb-3"
          />
          <h5 className="mb-3">You haven't placed any orders yet.</h5>
          <a href="/shop" className="btn btn-dark">
            Start Shopping
          </a>
        </div>
      ) : (
        <div className="row">
          {orders.map((order, index) => (
            <div key={index} className="col-md-6 col-lg-4 mb-4">
              <div className="card border-0 shadow-sm h-100">
                <div className="card-body">
                  <h5 className="card-title">Order #{index + 1}</h5>
                  <p className="text-muted mb-2">
                    <strong>Order Date:</strong> {order.orderDate}
                  </p>
                  <p>
                    <strong>Total Amount:</strong> ₹{order.total}
                  </p>
                  <h6 className="mt-3">Items:</h6>
                  <ul className="list-group list-group-flush mb-3">
                    {order.items.map((item, idx) => (
                      <li
                        key={idx}
                        className="list-group-item d-flex justify-content-between align-items-center"
                      >
                        <div className="d-flex align-items-center">
                          <img
                            src={item.imageUrl}
                            alt={item.name}
                            className="img-thumbnail me-3"
                            style={{
                              width: "50px",
                              height: "50px",
                              objectFit: "cover",
                            }}
                          />
                          <div>
                            <p className="mb-1">{item.name}</p>
                            <p className="mb-0">
                              {item.quantity} × ₹{item.price} = ₹
                              {item.quantity * item.price}
                            </p>
                          </div>
                        </div>
                      </li>
                    ))}
                  </ul>
                  <h6>Delivery Address:</h6>
                  <p className="small mb-0">
                    {order.address.streetNo && `${order.address.streetNo}, `}
                    {order.address.village}, {order.address.city},{" "}
                    {order.address.state}, {order.address.pincode}
                  </p>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default OrderHistory;
